mapper.select:
SELECT *
FROM articleReport
WHERE nickname = #{nickname}
AND articleIndex = #{articleIndex}

mapper.update:
UPDATE articleReport
SET articleDeleteReason = #{articleDeleteReason}, articleDeleteCheck = #{articleDeleteCheck} 
WHERE articleIndex = #{articleIndex}

mapper.select:
