mapper.select:
SELECT COUNT(email)
FROM members
WHERE email = #{email}

SELECT COUNT(nickname)
FROM members
WHERE nickname = #{nickname}

SELECT COUNT(contact)
FROM members
WHERE contact = #{contact}

mapper.update:

UPDATE members 
SET signAt = NOW() 
WHERE email = #{email}

mapper.select:
SELECT blockedMember
FROM memberBlock
WHERE nickname = #{nickname}
