mapper.select:
SELECT COUNT(email)
FROM members
WHERE email = #{email}

SELECT COUNT(nickname)
FROM members
WHERE nickname = #{nickname}

SELECT COUNT(contact)
FROM members
WHERE contact = #{contact}

mapper.update:

UPDATE members 
SET signAt = NOW() 
WHERE email = #{email}

UPDATE members
SET passwordAlertAt = NOW() + INTERVAL 3 MONTH
WHERE email = #{email}

UPDATE members
SET agreedTermAt = NOW()
WHERE email = #{email}


UPDATE members 
SET statusValue = 'MEM'
WHERE email = #{email}

mapper.select:
SELECT email
FROM members
WHERE name = #{name} 
AND sex = #{sex} 
AND birthDate = #{birthDate} 
AND telecomValue = #{telecomValue}
AND contact = #{contact}
LIMIT 1;

mapper.update:
UPDATE members
SET profileImageUrl = #{profileImageUrl}, nickname = #{nickname}
WHERE email = #{email}

UPDATE members 
SET 
password = #{password},
salt = #{salt}
WHERE email = #{email}


	UPDATE members 
 	SET alarmFlag = ${alarmFlag}, darkModeFlag = ${darkModeFlag}
 	WHERE email = #{email}

mapper.select:
